mapper.select:
SELECT COUNT(*)
FROM TB_APRV_INFO
WHERE USER_ID = #{userId}

SELECT FILE_NAME, FILE_SIZE
FROM TB_APRV_FILE
WHERE DOC_NO = #{docNo}

mapper.update:
UPDATE TB_APRV_LINE
SET APRV_STATUS = '03', OPINION = #{opinion}
WHERE USER_ID = #{userId}
AND DOC_NO = #{docNo}

		UPDATE TB_APRV_LINE A
		SET A.APRV_STATUS = '02'
		WHERE A.DOC_NO = #{docNo}
		AND EXISTS 
		(
    	SELECT APRV_SEQ
    	FROM TB_APRV_LINE B
    	WHERE B.DOC_NO = #{docNo}
    	AND B.USER_ID = #{userId}
   	 	AND A.APRV_SEQ = B.APRV_SEQ + 1
		)

UPDATE TB_APRV_LINE
SET APRV_STATUS = '04', OPINION = #{opinion}
WHERE USER_ID = #{userId}
AND DOC_NO = #{docNo}

UPDATE TB_APRV_INFO
SET DOC_STATUS = '03'
WHERE DOC_NO = #{docNo}
AND NOT EXISTS(
SELECT APRV_STATUS
FROM TB_APRV_LINE
WHERE DOC_NO = #{docNo}
AND APRV_STATUS IN ('01','02','04')
)

UPDATE TB_APRV_INFO
SET DOC_STATUS = '04'
WHERE DOC_NO = #{docNo}
AND EXISTS(
SELECT APRV_STATUS
FROM TB_APRV_LINE
WHERE DOC_NO = #{docNo}
AND APRV_STATUS = '04'
)

mapper.select:
SELECT NVL(MAX(APRV_SEQ), 0)
FROM TB_APRV_LINE
WHERE DOC_NO = #{docNo}

	SELECT NVL(MAX(FILE_SEQ), 0)
    FROM TB_APRV_FILE
    WHERE DOC_NO = #{docNo}
