mapper.select:
SELECT COUNT(*)
FROM TB_RCV_INFO A, TB_PARTNER_INFO B
WHERE 1=1
AND A.COMP_CD = B.COMP_CD

AND INVOICE_NO LIKE '%'||#{invoiceNo}||'%'


AND B.COMP_NAME = #{compName}


AND TO_CHAR(IN_DT, 'YYYY-MM-DD')  >=  #{inDtFrom}


 AND TO_CHAR(IN_DT, 'YYYY-MM-DD')  <=  #{inDtTo}

SELECT A.RCV_NO, TO_CHAR(A.IN_DT, 'YYYY-MM-DD') AS IN_DT, A.COMP_CD, B.COMP_NAME, A.INVOICE_NO, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT, SUM(C.IN_PRICE) AS IN_PRICE, SUM(C.IN_QTY) AS IN_QTY FROM TB_RCV_INFO A JOIN TB_PARTNER_INFO B ON A.COMP_CD = B.COMP_CD JOIN TB_RCV_ITEM C ON A.RCV_NO = C.RCV_NO WHERE 1=1
AND A.INVOICE_NO LIKE '%'||#{invoiceNo}||'%'
AND B.COMP_NAME LIKE '%'||#{compName}||'%'
AND TO_CHAR(A.IN_DT, 'YYYY-MM-DD') >= #{inDtFrom}
AND TO_CHAR(A.IN_DT, 'YYYY-MM-DD') <= #{inDtTo}
GROUP BY A.RCV_NO, A.IN_DT, A.COMP_CD, B.COMP_NAME, A.INVOICE_NO, A.REG_DT ORDER BY A.REG_DT DESC
